
public class Dowhilesample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 1;
		while(num <= 10)
		{
			System.out.println(num);
			num++;
		}
		System.out.println("_______________");
		do
		{
			num ++;
			System.out.println(num);
			
		} while (num != 14);
		
			}

	
}
