
public class ForLoopSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 1;
		while(num <= 10)
		{
			System.out.println(num);
			num++;
		}
		System.out.println("_______________");
		do
		{
			num ++;
			System.out.println(num);
			
		} while (num != 14);
		
		System.out.println("_______________");
		for (num = 10; num <= 15 ; num ++)
			System.out.println(num);
		
			}

	}


